class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)

        for i in range(n):
            nums[i] -= target[i]

        def f(l, r):
            res = 0
            for i in range(l + 1, r + 1):
                if abs(nums[i]) > abs(nums[i-1]):
                    res += abs(nums[i]) - abs(nums[i-1])
            return res + abs(nums[l])

        print(nums)
        j = 0
        ans = 0
        for i in range(n):
            if nums[i] * nums[j] <= 0:
                ans += f(j, i-1)
                j = i
        return ans + f(j, n - 1)
