public class Solution {
    public long MinimumOperations(int[] nums, int[] target) {
        long changes = 0;
        long currentChange = 0;
        long nextChange;
        for (int i = 0; i < nums.Length; ++i) {
            nextChange = target[i] - nums[i];
            if (nextChange > 0 && currentChange > 0) {
                if (nextChange > currentChange) {
                    changes += nextChange - currentChange;
                }
            } else if (nextChange < 0 && currentChange < 0) {
                if (nextChange < currentChange) {
                    changes -= nextChange - currentChange;
                }
            } else {
                changes += Math.Abs(nextChange);
            }
            currentChange = nextChange;
        }
        return changes;
    }
}
