class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        int[] diff = new int[n];
        for(int i = 0; i < n; i++) {
            diff[i] = nums[i] - target[i];
        }
        long res = 0L;
        long pre = 0L;
        for(int i = 0; i < n; i++) {
            if(!(pre > 0 && diff[i] > 0 && pre >= diff[i]) && !(pre < 0 && diff[i] < 0 && pre <= diff[i])) {
                res += Math.min(Math.abs(diff[i]), Math.abs(diff[i] - pre));
            }
            pre = diff[i];
        }
        return res;
    }
}
