using LL = long long;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        int diff[n];
        for(int i=0; i<n; i++) diff[i] = target[i]-nums[i];

        LL min_oper = 0;
        for(int l=0; l<n; l++){
            if(diff[l]==0) continue;
            if(diff[l]>0){
                int r = l;
                while(r<n && diff[r]>0) r++;

                int prev = 0;
                for(int i=l; i<r; i++){
                    if(diff[i]>prev) min_oper += diff[i]-prev;
                    prev = diff[i];
                }
                l = r-1;
            }
            else{
                int r = l;
                while(r<n && diff[r]<0) r++;

                int prev = 0;
                for(int i=l; i<r; i++){
                    if(abs(diff[i])>prev) min_oper += abs(diff[i])-prev;
                    prev = abs(diff[i]);
                }
                l = r-1;
            }
        }
        return min_oper;
    }
};
