class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long Result = 0;
		int i;
		int N = nums.size();
		long long  Now = 0;

		for(i = 0; i < N; i++)
		{
			long long  Temp = (nums[i] - target[i]);

			if(Temp > 0)
			{
				if(Now > 0)
				{
					if(Temp > Now)
					{
						Result += (Temp - Now);
					}
				}
				else
				{
					Result += Temp;
				}
			}
			else
			{
				if(Now > 0)
				{
					Result += abs(Temp);
				}
				else
				{
					if(Temp < Now)
					{
						Result += (Now - Temp);
					}
				}
			}

			Now = Temp;
		}

		return Result;
    }
};
