class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0, curops = 0;
        for (int i = 0; i < nums.size(); i++) {
            long long diff = target[i] - nums[i];
            if (diff < 0) {
                if (curops < 0) {
                    if (curops < diff)
                        curops = diff;
                    else {
                        ans += abs(curops - diff);
                        curops = diff;
                    }
                } else {
                    ans += abs(diff);
                    curops = diff;
                }
            } else if (diff > 0) {
                if (curops > 0) {
                    if (curops > diff)
                        curops = diff;
                    else {
                        ans += diff - curops;
                        curops = diff;
                    }
                } else {
                    ans += abs(diff);
                    curops = diff;
                }
            } else {
                curops = 0;
            }
        }

        return ans;
    }
};
