class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        # 感觉是同余
        n = len(nums)
        f = [nums[i] - target[i] for i in range(n)]
        st = ed = 0 
        ans = 0 
        while st < n:
            ed = st + 1
            while ed < n and f[ed] * f[ed - 1] > 0:
                ed += 1
            # [st, ed - 1]
            ans += abs(f[st])
            for i in range(st + 1, ed):
                a, b = abs(f[i - 1]), abs(f[i])
                if a >= b:
                    continue
                ans += b - a
            st = ed
        return ans
