class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& tar) {
        int n = nums.size();
        vector<int> tb;
        for(int i=0;i<n;i++) {
            tb.push_back(tar[i]-nums[i]);
        }
        long long res = 0;
        int last = 0;
        for(auto e:tb) {
            if(e!=0) {
                auto a=0;
                auto b=0;
                if(e>0) a=1;
                if(e<0) a=-1;
                if(last>0) b=1;
                if(last<0) b=-1;
                //cout<<last<<","<<e<<"="<<(last-e)
                if(a==b&&a!=0) {
                    if(e>last&&a==1) {
                        res+=(e-last);
                    }
                    if(e<last&&a==-1) {
                        res+=(last-e);
                    }
                }
                if(a!=b) {
                    res+=(e>0)?e:-e;
                }
            }
            //cout<<res<<endl;
            last = e;
        }
        return res;
    }
};
