class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        int flag=1;
        long long ans=0;
        long long temp=0;
        for(int i=0;i<n;i++){
            long long diff=nums[i]-target[i];
            if(diff>0){
                if(flag==-1){
                    flag=1;
                    temp=0;
                }
                if(diff<temp){
                    temp=diff;
                    continue;
                }
                else{
                    ans+=diff-temp;
                    temp=diff;
                }
            }
            else{
                if(flag==1){
                    flag=-1;
                    temp=0; 
                }
                diff=-diff;
                 if(diff<temp){
                    temp=diff;
                    continue;
                }
                else{
                    ans+=diff-temp;
                    temp=diff;
                }
            }
        }
        return ans;
    }
};
