class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        #define int long long
        int n = nums.size();
        int ans = 0, op = 0;
        for( int i = 0; i < n; ++i){
            if( nums[i] != target[i]){
                int req = target[i] - nums[i];
                if( req > 0){
                    if( req > op)   ans += min( req , req-op);
                }
                else{
                    if( req < op)   ans += min( -req ,op - req);
                }
                op = req;
            }
            else
                op = 0;
        }
        return ans;

        #undef int
    }
};
