class Solution {
public:
    long long evaluateSubarray(vector<int>& eval, int base) {
        if (eval.size() == 0) return 0;
        if (eval[0] < 0) {
            for (auto& val : eval) {
                val = -val;
            }
        }
        int operations = 0;
        vector<int> ops;
        vector<int> extra;
        for (auto val : eval) {
            int iters = 1;
            while (ops.size() > 0 && ops.back() > val) {
                iters++;
                extra.push_back(ops.back());
                ops.pop_back();
            }
            operations += evaluateSubarray(extra, val);
            extra.clear();
            while (iters--) {
                ops.push_back(val);
            }
        }
        return operations + ops.back() - base;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> eval;
        long long operations = 0;
        for (int i = 0; i < n; i++) {
            int diff = target[i] - nums[i];
            if (diff == 0) {
                operations += evaluateSubarray(eval, 0);
                eval.clear();
            } else {
                if (eval.size() == 0) {
                    eval.push_back(diff);
                    continue;
                }
                if (diff > 0) {
                    if (eval.back() > 0) {
                        eval.push_back(diff);
                    } else {
                        operations += evaluateSubarray(eval, 0);
                        eval.clear();
                        eval.push_back(diff);
                    }
                } else {
                    if (eval.back() < 0) {
                        eval.push_back(diff);
                    } else {
                        operations += evaluateSubarray(eval, 0);
                        eval.clear();
                        eval.push_back(diff);
                    }
                }
            }
        }
        operations += evaluateSubarray(eval, 0);
        return operations;
    }
};
