class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        lst = [nums[i] - target[i] for i in range(len(nums))]
        res = abs(lst[0])
        for i in range(1, len(lst)):
            if lst[i]*lst[i-1] <=0:
                res = res + abs(lst[i])
                continue
            if abs(lst[i]) < abs(lst[i-1]):
                continue
            res = res + abs(lst[i]) - abs(lst[i-1])
        return res
