'''
f[i][j] = i.j==   =>  f(i + 1, j), f(i, j-1)
f[i-1] > t:
f[i+1] = t
f[i-1]< t
f[i+1] = t

'''


class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        f = [0] * (n + 1)
        ans = 0

        for i, (x, y) in enumerate(zip(nums, target)):
            t = y - x
            if t >= 0:
                if f[i] >= 0:
                    if f[i] < t:
                        ans += t - f[i]
                else:
                    ans += t
            else:
                if f[i] >= 0:
                    ans -= t 
                else:
                    if f[i] > t:
                        ans += f[i] - t 
            f[i + 1] = t
        return ans
