class Solution:
    def minimumOperations(self, nums: list[int], target: list[int]) -> int:
        n = len(nums)
        diffs = [(target[i] - nums[i]) for i in range(n)]

        moves = abs(diffs[0])
        for i in range(1, n):
            prev = diffs[i - 1]
            cur = diffs[i]
            # print(prev, cur)
            if prev < 0 and cur < 0 or prev > 0 and cur > 0:
                prev = abs(prev)
                cur = abs(cur)
                moves += cur - min(prev, cur)
            else:
                moves += abs(cur)

        return moves
