/**
 * @param {number[]} nums
 * @param {number[]} target
 * @return {number}
 */
var minimumOperations = function(nums, target) {
    const d = [0];
    for (let i = 0; i < nums.length; i += 1) {
        d.push(nums[i] - target[i]);
    }
    let f = true;
    let res = 0;
    for (let i = 1; i < d.length; i += 1) {
        if (f) {
            if (d[i] >= 0) {
                if (d[i] > d[i - 1]) {
                    res += d[i] - d[i - 1];
                }
            } else {
                f = false;
                res -= d[i];
            }
        } else {
            if (d[i] <= 0) {
                if (d[i] < d[i - 1]) {
                    res += d[i - 1] - d[i];
                }
            } else {
                f = true;
                res += d[i];
            }
        }
    }
    return res;
};
