class Solution 
{

public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) 
    {
        long long ret = 0;
        int n = nums.size();
        vector<long long> f(n);

        for(int i = 0; i < n; i++)
        {
            f[i] = target[i] - nums[i];
        }

        int i = 0;
        while(i < n)
        {
            if(f[i] == 0) 
            {
                i++;
                continue;
            }

            int j = i + 1;
            ret += abs(f[i]);
            long long prev = abs(f[i]);

            while(j < n && f[j] * f[j - 1] > 0)
            {
                long long t = abs(f[j]);
                if(prev < t) ret += t - prev;
                prev = t;
                j++;
            }
            i = j;
        }

        return ret;
    }
};
