class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        size = len(nums)
        total = 0
        h = 0
        for i in range(size):
            t = nums[i] - target[i]
            if h >= 0 and t >= 0:
                if h >= t:
                    h = t
                    continue
                else:
                    total += t - h
                    h = t
            elif h <= 0 and t <= 0:
                if h <= t:
                    h = t
                    continue
                else:
                    total += h - t
                    h = t
            elif h >= 0 and t <= 0:
                total += -t
                h = t
                continue
            else:
                total += t
                h = t
        return total
