class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        long long ans = 0,n = a.size();
        long long z = 0,f = 0;
        vector<int> nums(n),target(n);
        nums[0] = a[0];
        target[0] = b[0];
        for(int i = 1;i < n;i++){
           nums[i] = a[i] - a[i-1];
           target[i] = b[i] - b[i-1];

        }
        for(int i = 0;i < n;i++){
            if(nums[i] > target[i]){
              z += nums[i] - target[i];
            }else if(nums[i] < target[i]){

                f += target[i] - nums[i];
            }
        }

        ans = max(z,f);
        return ans;
    }
};
