class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {

        long long ans = 0;
        for(int i=0;i<nums.size();i++)
        {
            if(i==0)
            {
                ans+=abs(nums[i]-t[i]);
            }
            else
            {
                if((nums[i]-t[i] <0   && nums[i-1]-t[i-1]<0)  ||  (((nums[i]-t[i])>0)  &&  (nums[i-1]-t[i-1] >0)))
                {
                    ans+=(abs(nums[i]-t[i])>abs(nums[i-1]-t[i-1])?abs((nums[i]-t[i])-(nums[i-1]-t[i-1])):0);
                }
                else
                {
                    ans+=abs(nums[i]-t[i]);
                }
            }

        }
            return ans;
    }
};
