class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        vector<int> diff;
        for(int i=0;i<nums.size();i++){
            diff.push_back(target[i]-nums[i]);
        }
        int cur=0;
        int last=0;
        int lastNum=0;
        for(int i=0;i<diff.size();i++){
            if(diff[i]>=0){
                cur=1;
            }
            else{
                cur=-1;
            }
            if(cur!=last){
                last=cur;
                lastNum=0;
            }
            int num=abs(diff[i]);
            if(lastNum<num){
                ans+=(num-lastNum);
            }
            lastNum=num;
        }
        return ans;
    }
};
