int a[100020];
int b[100020];
int c[100020];
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for(int i=0;i<n-1;++i){
            a[i+1] = nums[i+1] - nums[i];
            b[i+1] = target[i+1] - target[i];
        }
        a[0] = nums[0];
        b[0] = target[0];
        long long sum1 = 0;
        long long sum2 = 0;
        for(int i=0;i<n;++i){
            c[i] = a[i] - b[i];
            if(c[i] < 0) sum1 -= c[i];
            else sum2 += c[i];
        }

        return max(sum1, sum2);
    }
};
