class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for (int i = 0 ; i < nums.size(); i++) {
            nums[i] = target[i] - nums[i];
        }

        long long ans = 0;
        long long lastCount = 0;
        for (int i = 0; i < nums.size(); i++) {
            if (nums[i] == 0) {
                lastCount = 0;
                continue;
            }

            if (lastCount * nums[i] < 0) {
                lastCount = 0;
            }

            if (nums[i] > 0) {
                ans +=  max(nums[i] - lastCount, 0LL);
                lastCount = nums[i];
            } else {
                ans +=  abs(min(nums[i] - lastCount, 0LL));
                lastCount = nums[i];
            }
        }
        return ans;
    }
};
