#define ll long long

class Solution {

public:

    ll eat(vector<ll>& st){
        if(st.size() == 0)
            return 0;
        for(ll& i : st){
            i = abs(i);
        }
        ll res = st[0];
        for(int i = 1;i<st.size();i++){
            res += max((ll)0,st[i] - st[i-1]);
        }
        return res;
    }

    bool opp(int a, int b) {
        return (a < 0 && b > 0) || (a > 0 && b < 0);
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<ll> st;
        int n = nums.size();
        ll res = 0;
        for(int i = 0;i<n;i++){
            ll val = nums[i] - target[i];
            if((val == 0) || st.size() && opp(val,st[0])){
                res += eat(st);
                st.clear();
            }
            if(val)
                st.push_back(val);
        }
        res += eat(st);
        return res;
    }
};
