class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        a = [0] * (n + 2)

        for i in range(n):
            change = target[i] - nums[i]
            a[i + 1] += change
            a[i + 2] -= change

        pos, neg = 0, 0
        for i in range(1, n + 1):
            if a[i] > 0:
                pos += a[i]
            elif a[i] < 0:
                neg -= a[i]

        min_cnt = max(pos, neg)
        return min_cnt
