class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<int>v(n+1);
        for(int i = 0;i<n;i++){
            v[i]=nums[i]-target[i];
            //cout<<v[i]<<' ';
        }
        long long mx = 0;
        long long res = 0;
        for(int i = 0;i<n;i++){
            //
            mx = max(mx,(long long)abs(v[i]));
            if(v[i+1]==0||v[i]==0||v[i]/abs(v[i])!=v[i+1]/abs(v[i+1])){
                ans+=mx-res;
                mx=0;
                res = 0;
            } else if(abs(v[i])!=mx&&abs(v[i+1])>abs(v[i])){
                ans +=mx-res;
                mx = 0;
                res = abs(v[i]);
            }
            //cout<<i<<' '<<mx<<' '<<res<<' '<<ans<<endl;
        }
        return ans;
    }
};
