class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        for i in range(len(target)):
            target[i]-=nums[i]
        ans=abs(target[0])
        for i in range(1,len(target)):
            if target[i]>0:
                if target[i-1]>0:
                    ans+=max(target[i]-target[i-1],0)
                else:
                    ans+=target[i]
            elif target[i]<0:
                if target[i-1]<0:
                    ans+=max(target[i-1]-target[i],0)
                else:
                    ans-=target[i]
        return ans
