class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n=len(nums)
        p=[target[i]-nums[i] for i in range(n)]
        res=0
        for i in range(n):
            if p[i]==0: continue
            absPI=abs(p[i])
            ABS=absPI/p[i]

            res+=abs(p[i])

            j=i+1
            while(j<n and p[j]!=0 and abs(p[j])/p[j] ==ABS and absPI!=0):
                absPI=min(abs(p[j]),absPI)
                p[j]-=ABS*absPI
                j+=1
        return int(res)
