class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int N = nums.size();
        vector<int> d(N + 2);
        for (int i = 1; i <= N; ++i) d[i] = nums[i-1] - target[i-1];
        long long ans = 0;
	    for (int i = 0; i <= N; ++i) {
		    ans += abs(d[i] - d[i + 1]);
        }
        return ans / 2ll;
    }
};
