class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n=a.size();
        vector<long long>d(n+1);
        for(int i=1;i<=n;i++) d[i]=b[i-1]-a[i-1];

        long long ans=0;
        int ma=0;
        int mi=0;
        for(int i=1;i<=n;i++){
            if(d[i]==0) continue;
            else if(d[i]>0){
                mi=0;
                if(ma==0) ans+=d[i],ma=d[i];
                else{
                    if(d[i]<=d[i-1]) continue;
                    else{
                         ans+=d[i]-d[i-1];
                         ma=d[i];
                    }
                }
            }else{
                ma=0;
                if(mi==0) ans+=abs(d[i]),mi=d[i];
                else{
                    if(d[i]>=d[i-1]) continue;
                    else{
                         ans+=(abs(d[i])-abs(d[i-1]));
                         mi=d[i];
                    }
                }
            }
        }
        return ans;
    }
};
