class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        long long res=abs(a[0]-b[0]);
        int n=a.size();
        for (int i=1;i<n;i++) {
            if ((a[i]-b[i])*(long long)(a[i-1]-b[i-1])<=0) res+=abs(a[i]-b[i]);
            else {
                if (abs(a[i]-b[i])>=abs(a[i-1]-b[i-1])) res+=abs(a[i]-b[i])-abs(a[i-1]-b[i-1]);
            }
        }
        return res;
    }
};
