class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        nums[0] = nums[0] - target[0];
        long total = nums[0]>=0 ? nums[0] : -nums[0];
        for(int i=1; i<nums.length; i++) {
            nums[i] = nums[i] - target[i];
            if(nums[i]>=0 && nums[i-1]>=0) {
                if(nums[i-1]<nums[i]) {
                    total = total + nums[i] - nums[i-1];
                }
            } else if(nums[i]<=0 && nums[i-1]<=0) {
                if(nums[i-1]>nums[i]) {
                    total = total - nums[i] + nums[i-1];
                }
            } else {
                total = total + (nums[i]>=0 ? nums[i] : -nums[i]);
            }
        }
        return total;
    }
}
