long long solve(int l, int r, vector<int>&arr, int h) {
    if (l>r) return 0;
    while (l < r && arr[l] == h) ++l;
    while (l > r && arr[r] == h) --r;

    if (l>r) return 0;


    int mn_i = l;
    for (int i=l; i<=r; ++i) {
        if (abs(arr[i]) < abs(arr[mn_i])) {
            mn_i = i;
        }
    }

    return abs(arr[mn_i]) - h + solve(l, mn_i-1, arr, abs(arr[mn_i])) + solve(mn_i+1, r, arr, abs(arr[mn_i]));

}
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector <int> arr(n);
        for (int i=0; i<n; ++i) {
            arr[i] = target[i] - nums[i];
            //cout << arr[i] <<" ";
        }

        #define same(x,y) ((x < 0 && y < 0) || (x > 0 && y > 0) || (x == 0 && y == 0))

        long long ans = 0;
        int i = 0;
        while ( i < n ) {
            int j = i;
            while (j<n && same(arr[i], arr[j])) {
                ++j;
            }

            ans += solve(i, j-1, arr, 0);

            i = j;


        }
        return ans;

    }

};
