class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        int n  = nums.size();
        int a[n];

        for(int i = 0 ; i < n ; i++){
            a[i]  = target[i] - nums[i];
        }

        long long ans = 1ll*abs(a[0]);

        int diff = a[0];

        for(int i = 1; i < n ; i++){

            if(diff<0 && a[i]>=0){

                ans += 1ll*abs(a[i]);
                diff =a[i];


            }
            else if(diff>=0 && a[i]<0){
                 ans += 1ll*abs(a[i]);
                   diff = a[i];


            }
            else{

                if(abs(a[i]) > abs(diff)){
                    //  ans += abs(a[i]);
                     ans += 1ll*abs( abs(diff) - abs(a[i]));




                }

                diff=a[i];
            }

        }

        return ans;



    }
};
