class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> vec(n);
        for (int i=0; i<n; i++){
            vec[i] = target[i]-nums[i];
        }

        long long ans = 0, prefix = 0;
        for (long long v: vec){
            if (v==0){
                prefix = 0;
            }else if(v>0){
                if (v>prefix) ans += v-max((long long)0, prefix);
                prefix = v;
            }else if(v<0){
                if (v<prefix) ans += min((long long)0, prefix)-v;
                prefix = v;
            }
        }
        return ans;
    }
};
