class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        for(int i=0;i<nums.size();++i) {
            diff[i] = target[i]-nums[i];
        }
        long long ans = 0;
        // set<int> s;
        // for(auto &c:diff) {
        //     if(s.find(c)!=s.begin()) {
        //         s.insert(c);
        //         ans+=abs;
        //     }
        // }
        vector<long long> dp(n,-1);
        ans = solve(0,diff,dp);
        return ans;
    }

    long long solve(int i, vector<int> &diff, vector<long long> &dp){
        if(i==diff.size()) return 0;
        if(dp[i]!=-1) return dp[i];

        if(i+1==diff.size()) return dp[i] = abs(diff[i]);

        if(diff[i+1]>=diff[i] && diff[i]>=0) return dp[i] = solve(i+1,diff,dp);
        if(diff[i+1]<diff[i] && diff[i]>=0) return dp[i] = min(0ll+solve(i+1,diff,dp)+abs(diff[i]),0ll+solve(i+1,diff,dp)+diff[i]-diff[i+1]);
        if(diff[i+1]>=diff[i] && diff[i]<0) return dp[i] = min(0ll+solve(i+1,diff,dp)+abs(diff[i]),0ll+solve(i+1,diff,dp)+diff[i+1]-diff[i]);
        if(diff[i+1]<diff[i] && diff[i]<0) return dp[i] = solve(i+1,diff,dp);
        return 0;
    }
};
