class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        sol = 0

        minny = 0
        maxxy = 0
        for i in range(len(nums)):
            if target[i] > nums[i]:
                if minny > 0:

                    sol += minny
                    minny = 0
                if maxxy > target[i] - nums[i]:
                    sol += maxxy - target[i] + nums[i]

                maxxy = target[i] - nums[i]
                #maxxy += target[i] - nums[i]

            elif target[i] < nums[i]:
                if maxxy > 0:
                    sol += maxxy
                    maxxy = 0
                if minny > nums[i] - target[i]:
                    sol += minny - (nums[i] - target[i])

                minny = nums[i] - target[i]
            else:
                sol += maxxy + minny
                minny = 0
                maxxy = 0
        return sol + maxxy + minny
