class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = [target[i] - nums[i] for i in range(n)]  # 目标变化量
        ans = abs(diff[0])
        for i in range(1, n):
            # 如果diff[i]与diff[i - 1]同号，则可以通过延长diff[i - 1]上的操作节省一些次数
            if diff[i] > 0 and diff[i - 1] > 0:
                ans += diff[i] - diff[i - 1] if diff[i] > diff[i - 1] else 0
            elif diff[i] < 0 and diff[i - 1] < 0:
                ans += diff[i - 1] - diff[i] if diff[i] < diff[i - 1] else 0
            else:
                ans += abs(diff[i])
        return ans
