class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long result = 0;
        int factor = nums[0] < target[0] ? 1 : -1;
        int preCount = abs(nums[0] - target[0]);
        result += preCount;
        for (int i=1; i<nums.size(); i++) {
            if (nums[i] == target[i]) {
                preCount = 0;
                factor = 0;
                continue;
            }
            if (nums[i] < target[i]) {
                int diff = target[i] - nums[i];
                if (factor != 1) {
                    preCount = diff;
                    result += diff;
                    factor = 1;
                    continue;
                }
                if (diff >= preCount) {
                    result += diff - preCount;
                    preCount = diff;
                } else {
                    preCount = diff;
                }
                continue;
            }
            if (nums[i] > target[i]) {
                int diff = nums[i] - target[i];
                if (factor != -1) {
                    preCount = diff;
                    result += diff;
                    factor = -1;
                    continue;
                }
                if (diff >= preCount) {
                    result += diff - preCount;
                    preCount = diff;
                } else {
                    preCount = diff;
                }
            }
        }
        return result;
    }
};
