class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        stk = []
        n = len(nums)
        flag = nums[0] < target[0]
        mx = 0
        ans = 0
        for i in range(n):
            diff = abs(nums[i] - target[i])
            if nums[i] < target[i]:
                if flag:
                    if mx < diff:
                        ans += diff - mx
                        mx = diff
                    else:
                        mx = diff
                else:
                    flag = True
                    ans += diff
                    mx = diff
            else:
                if not flag:
                    if mx < diff:
                        ans += diff - mx
                        mx = diff
                    else:
                        mx = diff
                else:
                    flag = False
                    ans += diff
                    mx = diff
        return ans
