class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res=0;
        int curr=0;
        for(int i=0;i<nums.size();i++){
            int diff=target[i]-nums[i];
            if(diff>0){
                if(curr>diff)curr=diff;
                else if(curr>0){
                    res+=diff-curr;
                    curr=diff;
                }else{
                    res+=diff;
                    curr=diff;
                }
            }else{
                if(curr<diff)curr=diff;
                else if(curr<0){
                    res+=curr-diff;
                    curr=diff;
                }else{
                    res-=diff;
                    curr=diff;
                }
            }
        }
        return res;
    }
};
