function minimumOperations(arr: number[], arrT: number[]): number {
    console.log('___')
    let force = 0;
    let moves = 0;

    // let debug = []

    for(let i = 0; i < arr.length; i++) {
        const diff = arrT[i] - arr[i]
        // debug.push(diff)
        if ((force >= 0) && (diff >= 0)) {
            const change = Math.max(0, diff - force)
            moves += change
        } else if ((force < 0) && (diff < 0)) {
            const change = Math.max(0, force - diff)
            moves += change
        } else {
            const change = Math.abs(diff)
            moves += change
        }
        // const change = Math.min(Math.abs(diff), Math.abs(diff - force))
        // console.log(change)
        // moves += change
        force = diff
    }
    // console.log(debug.join(', '))
    return moves
};
