class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();

        /*vector<int> diff(n);
        for(int i = 0 ; i < n; ++i) {
            diff[i] = target[i] - nums[i];
        }*/

        bool isIncrese = false;
        bool isDecrese = false;
        int scale = 0;
        long long res = 0;
        for(int i = 0 ; i < n; ++i) {
            int diff = target[i] - nums[i];
            if(diff == 0) {
                isIncrese = false;
                isDecrese = false;
                scale = 0;
            }

            if(diff > 0) {
                if(isIncrese) {
                    if(abs(diff) > scale) {
                        res += abs(diff) - scale;
                    }
                } else {
                    res += abs(diff);

                    isIncrese = true;
                    isDecrese = false;
                }
            } else {
                if(isDecrese) {
                    if(abs(diff) > scale) {
                        res += abs(diff) - scale;
                    }
                } else {
                    res += abs(diff);

                    isIncrese = false;
                    isDecrese = true;;
                }
            }

            scale = abs(diff);
        }

        return res;
    }
};
