class Solution {
public:


    long long getOperations(vector<long long> & v){
        long long n = v.size();
        for(auto & val : v)if(val <0) val = -val;

        long long operations = v[0];
        for(int i = 0;i<n-1; i++){
             operations += 1ll *max(0ll,1ll*v[i+1] - 1ll  *v[i]);
        }

        return operations;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long> v;
        long long n = nums.size(), ans = 0;
        for(int i = 0;i<n;i++){
             if(v.empty() || (v.back() < 0) == (nums[i] - target[i] <0))v.push_back(nums[i] - target[i]);
             else {
                ans += getOperations(v);
                v.clear();
                i--;
             }

        }

        ans += getOperations(v);

        return ans;
    }
};
