using ll = long long;

class Solution {
public:
    long long minimumOperations(vector<int>& v1, vector<int>& v2) 
    {
        ll n = v1.size();
        ll ans = 0;
        for(ll i = 0; i < n; i++)
        {
            vector<ll> v;
            if(v1[i] == v2[i]) continue;
            else if(v1[i] < v2[i])
            {
                ll j = i;
                while(j < n && v1[j] < v2[j])
                {
                    v.push_back(v2[j] - v1[j]);
                    j++;
                }

                ll res = v[0];
                for(ll k = 1; k < v.size(); k++)
                {
                    res += max(0 * 1ll, v[k] - v[k - 1]);
                }
                ans += res;

                i = j - 1;
            }
            else
            {
                ll j = i;
                while(j < n && v1[j] > v2[j])
                {
                    v.push_back(v1[j] - v2[j]);
                    j++;
                }

                ll res = v[0];
                for(ll k = 1; k < v.size(); k++)
                {
                    res += max(0 * 1ll, v[k] - v[k - 1]);
                }
                ans += res;

                i = j - 1;
            }
        }
        return ans;
    }
};
