class Solution {
public:

    long long int solveMini( vector<int> &temp ){

        int n = temp.size() ;

        for( int i=0 ; i<n ; i++ ){
            temp[i] = abs(temp[i]) ;
        }

        long long ans = temp[0] ;
        for( int i=1 ; i<n ; i++ ){
            if( temp[i] >= temp[i-1] ) ans += (temp[i] - temp[i-1]) ;
        }
        // cout << temp.size() << "  " <<ans << endl ;
        temp.clear() ;
        return ans ;

    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        int n = nums.size() ;
        vector<int> dif(n) ;

        for( int i=0 ; i<n ; i++ ){
            dif[i] = nums[i] - target[i] ;
        }

        int maxi = INT_MIN ;
        long long ans = 0 ;
        vector<int> temp ;
        for( int i=0 ; i<n ; i++ ){

            if( temp.size() == 0 ){
                temp.push_back(dif[i]) ;
            }
            else if( dif[i] >=0 && temp[0] >=0  ){
                temp.push_back( dif[i] ) ;
            }
            else if( dif[i] < 0 && temp[0] < 0 ){
                temp.push_back(dif[i]) ;
            }
            else{
                ans += solveMini(temp) ;
                i-- ;
            }

        }

        if( temp.size() != 0 ){
            ans += solveMini(temp) ;
        }
        // cout << endl ;
        // cout << endl ;
        return ans ;

    }
};
