class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        stack<int> s;
        long long ans=0;
        for(int i=0;i<target.size();i++){
            target[i]-=nums[i];
            if(target[i]<0){
                if(s.empty() || s.top()>=0)
                    ans+=target[i]*(-1);
                else{
                    if(s.top()>target[i]){
                        ans+=s.top()-target[i];
                    }
                }
                s.push(target[i]);
            }
            else{
                if(s.empty()){
                    ans+=target[i];
                }
                else{
                    if(s.top()<target[i]){
                        ans+=target[i]-max(s.top(),0);
                    }
                }
                s.push(target[i]);
            }
        }
        return ans;
    }
};
