class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long res = 0;
        int n = nums.length;
        int beforeMul = 1;
        long beforeCount = 0;

        for(int i = 0; i < n; i++){
            int diff = target[i] - nums[i];
            if(diff * beforeMul >= 0){
                diff = Math.abs(diff);
                if(diff > beforeCount){
                    res += diff - beforeCount;
                    beforeCount = diff;
                }
                else{
                    beforeCount = diff;
                }
            }
            else{
                beforeMul = diff > 0 ? 1 : -1;
                beforeCount = Math.abs(diff);
                res += beforeCount;
            }
        }

        return res;
    }
}
