class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        const int n = nums.size();
        vector<int> f(n + 1);
        vector<int> g(n + 1);
        long long res = 0;
        for(int i = 0; i < n; ++i) insert(f, i, i, nums[i]);
        for(int i = 0; i < n; ++i) insert(g, i, i, target[i]);
        for(int i = 0; i <= n; ++i) {
            int d = g[i] - f[i];
            if(d > 0) res += d;
        }
        return res;
    }

    void insert(vector<int>& h, int l, int r, int val) {
        h[l] += val, h[r + 1] -= val;
    }
};
