class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int pre_diff = 0;
        long long res = 0;

        for(int i = 0; i < nums.size(); ++i) {
            int diff = target[i] - nums[i];

            if(diff > 0) {
                res += max(0, diff - max(0, pre_diff));
            } else if(diff < 0) {
                res += max(0, min(0, pre_diff) - diff);
            }

            pre_diff = diff;
        }

        return res;
    }
};
