class Solution {
public:

    long long solve(vector<long long>& A) {  
    if (A.size() == 0)  
        return 0;  
    long long ans = A[0];  
    int n = (int)A.size();
    for (int i = 1; i < n ; i++) {  
        ans += max(A[i] - A[i - 1], 0LL);  
    }  
    return ans;  
}  

    long long minimumOperations(vector<int>& a, vector<int>& b) {
        bool inc = 1;
        bool dec = 0;
        long long ans = 0;
        int n = (int)a.size();
        vector<long long>s;
        vector<long long>t;
        for(int i = 0 ; i < n ; i++) {
            if(a[i] < b[i] && dec) {
                ans += solve(t);
                t.clear();
                s.push_back(b[i] - a[i]);
                dec = 0;
                inc = 1;
            } else if(a[i] > b[i] && inc) {
                ans += solve(s);
                s.clear();
                t.push_back(a[i] - b[i]);
                dec = 1;
                inc = 0;
            } else if(a[i] == b[i]) {
                ans += solve(s);
                ans += solve(t);
                s.clear();
                t.clear();
                inc = 1;
                dec = 0;
            } else if(a[i] < b[i] && inc) {
                s.push_back(b[i] - a[i]);
            } else if(a[i] > b[i] && dec) {
                t.push_back(a[i] - b[i]);
            }
        }

        if(!s.empty()) {
            ans += solve(s);
        }
        if(!t.empty()) {
            ans += solve(t);
        }
        return ans;

    }
};
